"use strict";

const fs = require('fs');

module.exports = {
  list: function(directory, onlyDirectories, callback) {
    let files = fs.readdirSync(directory);

    if (onlyDirectories) {
      files = files.filter(function(file) {
        return fs.statSync(directory + file).isDirectory();
      });
    }

    callback(files);
  },
  watch: function(directory, onlyDirectories, callback, start) {
    if (start) {
      this.list(directory, onlyDirectories, callback);
    }
    let obj = this;
    fs.watch(this.folder, {}, function(eventType, filename) {
      if (eventType === 'rename') {
        obj.list(directory, onlyDirectories, callback);
      }
    });
  }
};
